import React from "react";
import axios from "axios";
import './css/style.css'


class App extends React.Component {
  state = {
    details: [],
    user: "",
    image: null,
    imagePreview: null,
    annotate: "",
    captions: [
      "airplanes",
      "cars",
      "birds",
      "cats",
      "deer",
      "dogs",
      "frogs",
      "horses",
      "ships",
      "trucks",
    ],
  };

  componentDidMount() {
    let data;

    axios
      .get("http://localhost:8000/wel/")
      .then((res) => {
        data = res.data;
        this.setState({
          details: data,
        });
      })
      .catch((err) => {});
  }

  handleInput = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  handleImageChange = (e) => {
    const file = e.target.files[0];

    this.setState({
      image: file,
    });

    const reader = new FileReader();

    reader.onloadend = () => {
      this.setState({
        imagePreview: reader.result,
      });
    };

    if (file) {
      reader.readAsDataURL(file);
    }
  };

  handleDrop = (e) => {
    e.preventDefault();

    const file = e.dataTransfer.files[0];

    this.setState({
      image: file,
    });

    const reader = new FileReader();

    reader.onloadend = () => {
      this.setState({
        imagePreview: reader.result,
      });
    };

    if (file) {
      reader.readAsDataURL(file);
    }
  };

  handleSubmit = (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append("name", this.state.user);
    formData.append("image", this.state.image);
    formData.append("annotate", this.state.annotate);

    axios
      .post("http://localhost:8000/wel/", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      })
      .then((res) => {
        console.log("res = ", res);
        this.setState({
          user: "",
          image: null,
          imagePreview: null,
          annotate: "",
        });
      })
      .catch((err) => {
        console.error("Error:", err);
      });
  };

  render() {
    return (
      <div className="container">
        <div className="jumbotron">
          <h1 className="display-4 text-center">Image Upload Form</h1>
          <form onSubmit={this.handleSubmit}>
            <div className="form-group">
              <label htmlFor="authorInput">Author</label>
              <input
                type="text"
                className="form-control"
                id="authorInput"
                placeholder="Name of the Poet/Author"
                value={this.state.user}
                name="user"
                onChange={this.handleInput}
              />
            </div>

            <div
              className="form-group drop-zone"
              onDrop={this.handleDrop}
              onDragOver={(e) => e.preventDefault()}
            >
              <label>Upload Image</label>
              <input
                type="file"
                className="form-control-file"
                id="imageInput"
                onChange={this.handleImageChange}
              />
              {this.state.imagePreview && (
                <img
                  src={this.state.imagePreview}
                  alt="Preview"
                  className="img-thumbnail mt-2"
                  style={{ maxWidth: "200px", maxHeight: "200px" }}
                />
              )}
              <p className="text-center mt-2">
                Drag &amp; drop your image here, or click to select
              </p>
            </div>

            <div className="form-group">
              <label htmlFor="captionSelect">Caption</label>
              <select
                className="form-control"
                id="captionSelect"
                value={this.state.annotate}
                onChange={this.handleInput}
                name="annotate"
              >
                <option value="">Select annotate</option>
                {this.state.captions.map((annotate, index) => (
                  <option key={index} value={annotate}>
                    {annotate}
                  </option>
                ))}
              </select>
            </div>

            <button type="submit" className="btn btn-primary btn-block">
              Submit
            </button>
          </form>
        </div>

        <hr />

        <div className="row">
          {this.state.details.map((detail, id) => (
            <div key={id} className="col-md-3 mb-4">
              <div className="card">
                <img
                  src={"../../lucky/img/images/" + detail.image_path}
                  alt="Image"
                  className="card-img-top"
                />
                <div className="card-body">
                  <h5 className="card-title">Image {id + 1}</h5>
                  <p className="card-text">Author: {detail.author}</p>
                  <p className="card-text">Caption: {detail.caption}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  }
}

export default App;


