import React from "react";
import axios from "axios";
import './css/style.css'

class App extends React.Component {
  state = {
    details: [],
    user: "",
    image: null,
    imagePreview: null,
    // annotate: "",
    annotations: [], 
    captions: ["airplanes", "cars", "birds", "cats", "deer", "dogs", "frogs", "horses", "ships", "trucks"],
    draggedImagePreview: null,
  };

  componentDidMount() {
    let data;

    axios
      .get("http://localhost:8000/wel/")
      .then((res) => {
        data = res.data;
        this.setState({
          details: data,
        });
      })
      .catch((err) => { });
  }

  // handleInput = (e) => {
  //   this.setState({
  //     [e.target.name]: e.target.value,
  //   });
  // };

  handleInput = (e, index) => {
    const { annotations } = this.state;
    const newAnnotations = [...annotations];
    newAnnotations[index] = e.target.value;
    this.setState({ annotations: newAnnotations });
  };
  handleImageChange = (e) => {
    const file = e.target.files[0];

    this.setState({
      image: file,
    });

    const reader = new FileReader();

    reader.onloadend = () => {
      this.setState({
        imagePreview: reader.result,
      });
    };

    if (file) {
      reader.readAsDataURL(file);
    }
  };



  handleDrop = (e) => {
    e.preventDefault();
    const file = e.dataTransfer.files[0];

    this.setState({
      image: file,
    });

    const reader = new FileReader();

    reader.onloadend = () => {
      this.setState({
        imagePreview: reader.result,
        draggedImagePreview: null,
      });
    };

    if (file) {
      reader.readAsDataURL(file);
    }
  };

  handleDragOver = (e) => {
    e.preventDefault();
    e.stopPropagation();
  };

  // handleDragEnter = (e) => {
  //   e.preventDefault();
  //   e.stopPropagation();

  //   const file = e.dataTransfer.items[0].getAsFile();
  //   const reader = new FileReader();

  //   reader.onloadend = () => {
  //     this.setState({
  //       draggedImagePreview: reader.result,
  //     });
  //   };

  //   if (file) {
  //     reader.readAsDataURL(file);
  //   }
  // };
  handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    console.log("in handleDrop");
    console.log(e.dataTransfer.files[0]);
    const file = e.dataTransfer.files[0];

    this.setState({
      image: file,
    });

    const reader = new FileReader();

    reader.onloadend = () => {
      this.setState({
        imagePreview: reader.result,
        draggedImagePreview: null, // Clear dragged image preview
      });
    };

    if (file) {
      reader.readAsDataURL(file);
    }
  };

  render() {
    return (
      <div className="container">
        <div className="jumbotron">
          <h1 className="display-4 text-center">Image Upload Form</h1>
          <form onSubmit={this.handleSubmit}>
            <div className="form-group">
              <label htmlFor="authorInput">Author</label>
              <input
                type="text"
                className="form-control"
                id="authorInput"
                placeholder="Name of the Poet/Author"
                value={this.state.user}
                name="user"
                onChange={this.handleInput}
              />
            </div>

            <div className="form-group">
              <label htmlFor="imageInput">Upload Image</label>
              <input
                type="file"
                className="form-control-file"
                id="imageInput"
                onChange={this.handleImageChange}
              />
              <div
                className="dropzone"
                onDragOver={this.handleDragOver}
                // onDragEnter={this.handleDragEnter}
                // onDragLeave={this.handleDragLeave}
                onDrop={this.handleDrop}
              >
                Drop your image here or click to select a file
                {this.state.draggedImagePreview && (
                  <div className="preview">
                    <img
                      src={this.state.draggedImagePreview}
                      alt="Preview"
                      className="img-thumbnail"
                      style={{ maxWidth: "200px", maxHeight: "200px" }}
                    />
                  </div>
                )}
              </div>
            </div>

            {this.state.imagePreview && (
              <div className="form-group text-center">
                <img
                  src={this.state.imagePreview}
                  alt="Preview"
                  className="img-thumbnail"
                  style={{ maxWidth: "200px", maxHeight: "200px" }}
                />
              </div>
            )}

            <div className="form-group">
              <label htmlFor="captionSelect">Caption</label>
              <select
                className="form-control"
                id="captionSelect"
                value={this.state.annotate}
                onChange={this.handleInput}
                name="annotate"
              >
                <option value="">Select annotate</option>
                {this.state.captions.map((annotate, index) => (
                  <option key={index} value={annotate}>
                    {annotate}
                  </option>
                ))}
              </select>
            </div>

            <button type="submit" className="btn btn-primary btn-block">
              Submit
            </button>
          </form>
        </div>

        <hr />

        <div className="row">
          {this.state.details.map((detail, id) => (
            <div key={id} className="col-md-3 mb-4">
              <div className="card">
                <img
                  src={"../../lucky/img/images/" + detail.image_path}
                  alt="Image"
                  className="card-img-top"
                />
                <div className="card-body">
                  <h5 className="card-title">Image {id + 1}</h5>
                  <p className="card-text">Author: {detail.author}</p>
                  <p className="card-text">Caption: {detail.caption}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  }
}

export default App;
