import React from "react";
import axios from "axios";
import './css/style.css'

class App extends React.Component {
  state = {
    details: [],
    user: "",
    image: null,
    imagePreview: null,
    annotations: {}, // Store annotations for each image
    draggedImagePreview: null,
  };

  componentDidMount() {
    let data;

    axios
      .get("http://localhost:8000/wel/")
      .then((res) => {
        data = res.data;
        this.setState({
          details: data,
        });
      })
      .catch((err) => { });
  }

  handleInput = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  handleImageChange = (e) => {
    const file = e.target.files[0];

    this.setState({
      image: file,
    });

    const reader = new FileReader();

    reader.onloadend = () => {
      this.setState({
        imagePreview: reader.result,
      });
    };

    if (file) {
      reader.readAsDataURL(file);
    }
  };

  handleDrop = (e) => {
    e.preventDefault();
    const file = e.dataTransfer.files[0];

    this.setState({
      image: file,
    });

    const reader = new FileReader();

    reader.onloadend = () => {
      this.setState({
        imagePreview: reader.result,
        draggedImagePreview: null,
      });
    };

    if (file) {
      reader.readAsDataURL(file);
    }
  };

  handleDragOver = (e) => {
    e.preventDefault();
    e.stopPropagation();
  };

  handleAnnotationChange = (id, value) => {
    // Update the annotations for the image with the given id
    this.setState((prevState) => ({
      annotations: {
        ...prevState.annotations,
        [id]: value,
      },
    }));
  };

  render() {
    return (
      <div className="container">
        <div className="jumbotron">
          <h1 className="display-4 text-center">Image Upload Form</h1>
          <form onSubmit={this.handleSubmit}>
            {/* Form inputs */}
          </form>
        </div>

        <hr />

        <div className="row">
          {this.state.details.map((detail, id) => (
            <div key={id} className="col-md-3 mb-4">
              <div className="card">
                <img
                  src={"../../lucky/img/images/" + detail.image_path}
                  alt="Image"
                  className="card-img-top"
                />
                <div className="card-body">
                  <h5 className="card-title">Image {id + 1}</h5>
                  <p className="card-text">Author: {detail.author}</p>
                  {/* Dropdown menu for annotation */}
                  <div className="form-group">
                    <label htmlFor={`annotationSelect_${id}`}>Annotation</label>
                    <select
                      className="form-control"
                      id={`annotationSelect_${id}`}
                      value={this.state.annotations[id] || ''}
                      onChange={(e) => this.handleAnnotationChange(id, e.target.value)}
                    >
                      <option value="">Select annotation</option>
                      {this.state.captions.map((annotate, index) => (
                        <option key={index} value={annotate}>
                          {annotate}
                        </option>
                      ))}
                    </select>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  }
}

export default App;
