import React from "react";
import axios from "axios";

class App extends React.Component {
  state = {
    details: [],
    user: "",
    image: null, // State to store uploaded image
    imagePreview: null, // State to store image preview
    annotate: "", // State to store selected annotation
    captions: ["airplanes", "cars", "birds", "cats", "deer", "dogs", "frogs", "horses", "ships", "trucks"], // Dropdown options
  };

  componentDidMount() {
    this.fetchData();
  }

  fetchData = () => {
    axios
      .get("http://localhost:8000/wel/")
      .then((res) => {
        const data = res.data;
        this.setState({
          details: data,
        });
      })
      .catch((err) => { });
  }

  renderSwitch = (param) => {
    switch (param + 1) {
      case 1:
        return "primary ";
      case 2:
        return "secondary";
      case 3:
        return "success";
      case 4:
        return "danger";
      case 5:
        return "warning";
      case 6:
        return "info";
      default:
        return "yellow";
    }
  };

  handleInput = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  handleImageChange = (e) => {
    const file = e.target.files[0];

    // Update image state
    this.setState({
      image: file,
    });

    // Create a FileReader instance
    const reader = new FileReader();

    // Listen for the FileReader load event
    reader.onloadend = () => {
      this.setState({
        imagePreview: reader.result, // Set imagePreview to the base64 encoded image data
      });
    };

    // Read the image file as a data URL
    if (file) {
      reader.readAsDataURL(file);
    }
  };

  handleSubmit = (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append("name", this.state.user);
    formData.append("image", this.state.image);
    // formData.append("annotate", this.state.annotate);
    formData.append("annotations", JSON.stringify(this.state.annotations)); // Convert annotations array to JSON string

    axios
      .post("http://localhost:8000/wel/", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      })
      .then((res) => {
        console.log("res = ", res);
        this.fetchData(); // Fetch data again to update the UI with the new upload
        this.setState({
          user: "",
          image: null,
          imagePreview: null,
          // annotate: "",
          annotations: [],
        });
      })
      .catch((err) => {
        console.error("Error:", err);
      });
  };

  handleAnnotationChange = (e) => {
    const selectedOptions = Array.from(e.target.selectedOptions, (option) => option.value);
    this.setState({ annotations: selectedOptions });
  };


  render() {
    const pendingUploads = this.state.imagePreview ? (
      <div>
        <h5>Pending Upload</h5>
        <img
          src={this.state.imagePreview}
          alt="Preview"
          className="img-fluid mt-3"
          style={{ maxWidth: "200px" }}
        />
      </div>
    ) : null;

    return (
      <div className="container jumbotron ">
        <div className="row">
          <div className="col-md-6">
            <form onSubmit={this.handleSubmit}>
              {pendingUploads}

              <div className="input-group mb-3">
                <div className="input-group-prepend">
                  <span className="input-group-text" id="basic-addon1">
                    {" "}
                    Author{" "}
                  </span>
                </div>
                <input
                  type="text"
                  className="form-control"
                  placeholder="Name of the Poet/Author"
                  aria-label="Username"
                  aria-describedby="basic-addon1"
                  value={this.state.user}
                  name="user"
                  onChange={this.handleInput}
                />
              </div>

              <div className="input-group mb-3">
                <div className="input-group-prepend">
                  <span className="input-group-text">Upload Image</span>
                </div>
                <input
                  type="file"
                  className="form-control"
                  onChange={this.handleImageChange}
                />
              </div>

              <div className="input-group mb-3">
                <div className="input-group-prepend">
                  <span className="input-group-text">Caption</span>
                </div>
                <select
                  className="form-control"
                  value={this.state.annotations}
                  onChange={this.handleAnnotationChange}
                  name="annotations"
                  multiple // Add the 'multiple' attribute to enable multiple selections
                >
                  {this.state.captions.map((annotate, index) => (
                    <option key={index} value={annotate}>
                      {annotate}
                    </option>
                  ))}
                </select>
              </div>

              <button type="submit" className="btn btn-primary mb-5">
                Submit
              </button>
            </form>
          </div>

          <div className="col-md-6">
            <h5>Uploaded Images</h5>
            {this.state.details.map((detail, id) => (
              <div key={id}>
                <h6>Author: {detail.name}</h6>
                <img
                  src={"http://localhost:8000/" + detail.image_path}
                  alt="Image"
                  className="img-fluid mt-3"
                />
                <div className="annotations">
                  <h6>Annotations:</h6>
                  <ul>
                    {detail.annotations.map((annotation, index) => (
                      <li key={index}>{annotation}</li>
                    ))}
                  </ul>
                </div>
                <span className="border border-primary "></span>
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  }
}

export default App;
