import Multiselect from 'multiselect-react-dropdown';
import React from "react";
import axios from "axios";
import 'bootstrap/dist/css/bootstrap.min.css';
import '../App.css'; // Import custom CSS for styling

class HomePage extends React.Component {
    state = {
        details: [],
        user: "",
        image: null, // State to store uploaded image
        imagePreview: null, // State to store image preview
        annotate: "", // State to store selected annotation
        captions: ["None", "airplanes", "cars", "birds", "cats", "deer", "dogs", "frogs", "horses", "ships", "trucks"], // Dropdown options
        annotations: ["None"],
        captionOptions: [],
    };


    // componentDidMount() {
    //     let data;

    //     axios
    //         .get("http://localhost:8000/wel/")
    //         .then((res) => {
    //             data = res.data;
    //             this.setState({
    //                 details: data,
    //             });
    //         })
    //         .catch((err) => { });
    // }
    componentDidMount() {
        let data;

        axios
            .get("http://localhost:8000/wel/")
            .then((res) => {
                data = res.data;
                this.setState({
                    details: data,
                    captionOptions: this.state.captions.map((caption) => ({
                        key: caption,
                        value: caption,
                    })),
                });
            })
            .catch((err) => { });
    }



    handleInput = (e) => {
        this.setState({
            [e.target.name]: e.target.value,
        });
    };

    handleImageChange = (e) => {
        const file = e.target.files[0];

        // Update image state
        this.setState({
            image: file,
        });

        // Create a FileReader instance
        const reader = new FileReader();

        // Listen for the FileReader load event
        reader.onloadend = () => {
            this.setState({
                imagePreview: reader.result, // Set imagePreview to the base64 encoded image data
            });
        };

        // Read the image file as a data URL
        if (file) {
            reader.readAsDataURL(file);
        }
    };

    handleSubmit = (e) => {
        e.preventDefault();

        const formData = new FormData();
        formData.append("name", this.state.user);
        formData.append("image", this.state.image);
        formData.append("annotations", JSON.stringify(this.state.annotations));

        axios
            .post("http://localhost:8000/wel/", formData, {
                headers: {
                    "Content-Type": "multipart/form-data",
                },
            })
            .then((res) => {
                console.log("res = ", res);

                // Make a GET request to fetch the updated data after a successful POST
                axios
                    .get("http://localhost:8000/wel/")
                    .then((response) => {
                        this.setState({
                            details: response.data,
                            user: "",
                            image: null,
                            imagePreview: null,
                            annotations: [],
                        });
                    })
                    .catch((err) => {
                        console.error("Error fetching data:", err);
                    });
            })
            .catch((err) => {
                console.error("Error:", err);
            });
    };
    //   handleSubmit = (e) => {
    //     e.preventDefault();

    //     const formData = new FormData();
    //     formData.append("name", this.state.user);
    //     formData.append("image", this.state.image);
    //     // formData.append("annotate", this.state.annotate);
    //     formData.append("annotations", JSON.stringify(this.state.annotations)); // Convert annotations array to JSON string

    //     axios
    //       .post("http://localhost:8000/wel/", formData, {
    //         headers: {
    //           "Content-Type": "multipart/form-data",
    //         },
    //       })
    //       .then((res) => {
    //         console.log("res = ", res);
    //         this.setState({
    //           user: "",
    //           image: null,
    //           imagePreview: null,
    //           // annotate: "",
    //           annotations: [],
    //         });
    //       })
    //       .catch((err) => {
    //         console.error("Error:", err);
    //       });
    //   };

    // handleAnnotationChange = (e) => {
    //     const selectedOptions = Array.from(e.target.selectedOptions, (option) => option.value);
    //     this.setState({ annotations: selectedOptions });
    // };

    render() {
        const pendingImages = this.state.details.filter(detail => detail.annotations.includes("None"));
        const annotatedImages = this.state.details.filter(detail => !detail.annotations.includes("None"));

        return (
            <div className="container py-5">
                <div className="row justify-content-center">
                    <div className="col-md-8">
                        <div className="card shadow-sm">
                            <div className="card-body">
                                <h2 className="card-title text-center mb-4">Image Annotation</h2>
                                <form onSubmit={this.handleSubmit}>
                                    <div className="form-group">
                                        <label htmlFor="author">Author</label>
                                        <input
                                            type="text"
                                            className="form-control"
                                            id="author"
                                            placeholder="Enter author name"
                                            value={this.state.user}
                                            name="user"
                                            onChange={this.handleInput}
                                        />
                                    </div>

                                    <div className="form-group">
                                        <label htmlFor="imageUpload">Upload Image</label>
                                        <div className="custom-file">
                                            <input
                                                type="file"
                                                className="custom-file-input"
                                                id="imageUpload"
                                                onChange={this.handleImageChange}
                                            />
                                            <label className="custom-file-label" htmlFor="imageUpload">
                                                Choose file
                                            </label>
                                        </div>
                                    </div>

                                    {this.state.imagePreview && (
                                        <div className="text-center mt-3">
                                            <img
                                                src={this.state.imagePreview}
                                                alt="Preview"
                                                className="img-fluid"
                                                style={{ maxWidth: "300px" }}
                                            />
                                        </div>
                                    )}

                                    <div className="form-group">
                                        <label htmlFor="annotations">Annotations</label>
                                        <Multiselect
                                            options={this.state.captionOptions} // Use captionOptions instead of captions
                                            selectedValues={this.state.annotations}
                                            onSelect={this.handleAnnotationChange}
                                            onRemove={this.handleAnnotationChange}
                                            displayValue="key"
                                            showCheckboxes={true}
                                            placeholder="Select annotations"
                                            style={{
                                                searchBox: {
                                                    border: "1px solid #ccc",
                                                    padding: "8px 12px",
                                                    borderRadius: "4px",
                                                },
                                            }}
                                        />
                                    </div>
                                    <button type="submit" className="btn btn-primary btn-block mt-4">
                                        Submit
                                    </button>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>

                <div className="row mt-5">
                    <div className="col-md-6">
                        <h3 className="text-center mb-4">Pending Images</h3>
                        <div className="image-grid">
                            {pendingImages.map((detail, id) => (
                                <div key={id} className="image-card">
                                    <div className="card-body">
                                        <h5 className="card-title">User: {detail.name}</h5>
                                        <img
                                            src={detail.image_url}
                                            alt="Image"
                                            className="img-fluid"
                                        />
                                        <div className="mt-3">
                                            <h6>Annotations:</h6>
                                            <ul className="list-unstyled">
                                                {detail.annotations.map((annotation, index) => (
                                                    <li key={index}>{annotation}</li>
                                                ))}
                                            </ul>
                                        </div>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                    {/* <div className="col-md-6 vertical-line"></div>  */}
                    <div className="col-md-6">
                        <h3 className="text-center mb-4">Annotated Images</h3>
                        <div className="image-grid">
                            {annotatedImages.map((detail, id) => (
                                <div key={id} className="image-card">
                                    <div className="card-body">
                                        <h5 className="card-title">User: {detail.name}</h5>
                                        <img
                                            src={detail.image_url}
                                            alt="Image"
                                            className="img-fluid"
                                        />
                                        <div className="mt-3">
                                            <h6>Annotations:</h6>
                                            <ul className="list-unstyled">
                                                {detail.annotations.map((annotation, index) => (
                                                    <li key={index}>{annotation}</li>
                                                ))}
                                            </ul>
                                        </div>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default HomePage;
