import React from "react";
import axios from "axios";
import 'bootstrap/dist/css/bootstrap.min.css';
import '../App.css'; // Import custom CSS for styling
import Jimp from 'jimp';

class HomePage extends React.Component {
    state = {
        details: [],
        user: "",
        image: null, // State to store uploaded image
        imagePreview: null, // State to store image preview
        annotate: "", // State to store selected annotation
        captions: ["None", "airplanes", "cars", "birds", "cats", "deer", "dogs", "frogs", "horses", "ships", "trucks"], // Dropdown options
        annotations: ["None"],
        resizeWidth: 300, // Default resize width
        resizeHeight: 300, // Default resize height
        selectedFilter: "none",
    };


    componentDidMount() {
        let data;

        axios
            .get("http://localhost:8000/wel/")
            .then((res) => {
                data = res.data;
                this.setState({
                    details: data,
                });
            })
            .catch((err) => { });
    }

    handleInput = (e) => {
        this.setState({
            [e.target.name]: e.target.value,
        });
    };
    handleResizeInputChange = (e) => {
        this.setState({
            [e.target.name]: parseInt(e.target.value),
        });
    };

    handleFilterChange = (e) => {
        this.setState({
            selectedFilter: e.target.value,
        });
    };

    // handleImageChange = (e) => {
    //     const file = e.target.files[0];

    //     // Update image state
    //     this.setState({
    //         image: file,
    //     });

    //     // Create a FileReader instance
    //     const reader = new FileReader();

    //     // Listen for the FileReader load event
    //     reader.onloadend = () => {
    //         this.setState({
    //             imagePreview: reader.result, // Set imagePreview to the base64 encoded image data
    //         });
    //     };

    //     // Read the image file as a data URL
    //     if (file) {
    //         reader.readAsDataURL(file);
    //     }
    // };
    handleImageChange = async (e) => {
        const file = e.target.files[0];

        // Read the image file using FileReader
        const reader = new FileReader();

        reader.onload = async (event) => {
            // Read the image data URL
            const dataUrl = event.target.result;

            // Create a Jimp image from the data URL
            const image = await Jimp.read(dataUrl);

            // Resize the image to custom dimensions
            image.resize(this.state.resizeWidth, this.state.resizeHeight);

            // Apply selected filter if any
            if (this.state.selectedFilter !== "none") {
                image[this.state.selectedFilter]();
            }

            // Convert the processed image to a data URL
            const processedDataUrl = await image.getBase64Async(Jimp.AUTO);

            // Set the processed image data URL to the state
            this.setState({
                imagePreview: processedDataUrl,
            });
        };

        // Read the image file as a data URL
        reader.readAsDataURL(file);
    };

    handleSubmit = (e) => {
        e.preventDefault();

        const formData = new FormData();
        formData.append("name", this.state.user);
        formData.append("image", this.state.image);
        formData.append("annotations", JSON.stringify(this.state.annotations));

        axios
            .post("http://localhost:8000/wel/", formData, {
                headers: {
                    "Content-Type": "multipart/form-data",
                },
            })
            .then((res) => {
                console.log("res = ", res);

                // Make a GET request to fetch the updated data after a successful POST
                axios
                    .get("http://localhost:8000/wel/")
                    .then((response) => {
                        this.setState({
                            details: response.data,
                            user: "",
                            image: null,
                            imagePreview: null,
                            annotations: [],
                        });
                    })
                    .catch((err) => {
                        console.error("Error fetching data:", err);
                    });
            })
            .catch((err) => {
                console.error("Error:", err);
            });
    };

    handleAnnotationChange = (e) => {
        const selectedOptions = Array.from(e.target.selectedOptions, (option) => option.value);
        this.setState({ annotations: selectedOptions });
    };

    render() {
        const pendingImages = this.state.details.filter(detail => detail.annotations.includes("None"));
        const annotatedImages = this.state.details.filter(detail => !detail.annotations.includes("None"));

        return (
            <div className="container py-5">
                <div className="row justify-content-center">
                    <div className="col-md-8">
                        <div className="card shadow-sm">
                            <div className="card-body">
                                <h2 className="card-title text-center mb-4">Image Annotation</h2>
                                <form onSubmit={this.handleSubmit}>
                                    <div className="form-group">
                                        <label htmlFor="author" style={{ fontSize: "20px" }}>Author</label>
                                        <input
                                            type="text"
                                            className="form-control"
                                            id="author"
                                            placeholder="Enter author name"
                                            value={this.state.user}
                                            name="user"
                                            onChange={this.handleInput}
                                        />
                                    </div>

                                    <div className="form-group">
                                        <label htmlFor="imageUpload" style={{ fontSize: "20px" }}>Upload Image</label>
                                        <br />

                                        <div className="custom-file">
                                            <input
                                                type="file"
                                                className="custom-file-input"
                                                id="imageUpload"
                                                onChange={this.handleImageChange}
                                            />

                                        </div>
                                    </div>
                                    <div className="form-group">
                                        <label htmlFor="resizeWidth">Resize Width:</label>
                                        <input
                                            type="number"
                                            id="resizeWidth"
                                            name="resizeWidth"
                                            value={this.state.resizeWidth}
                                            onChange={this.handleResizeInputChange}
                                            min="1"
                                        />
                                        <label htmlFor="resizeHeight">Resize Height:</label>
                                        <input
                                            type="number"
                                            id="resizeHeight"
                                            name="resizeHeight"
                                            value={this.state.resizeHeight}
                                            onChange={this.handleResizeInputChange}
                                            min="1"
                                        />
                                    </div>

                                    {/* Dropdown for selecting filters */}
                                    <div className="form-group">
                                        <label htmlFor="filters">Select Filter:</label>
                                        <select
                                            id="filters"
                                            value={this.state.selectedFilter}
                                            onChange={this.handleFilterChange}
                                        >
                                            <option value="none">None</option>
                                            <option value="greyscale">Greyscale</option>
                                            <option value="invert">Invert</option>
                                            {/* Add more filter options as needed */}
                                        </select>
                                    </div>

                                    {this.state.imagePreview && (
                                        <div className="text-center mt-3">
                                            <img
                                                src={this.state.imagePreview}
                                                alt="Preview"
                                                className="img-fluid"
                                                style={{ maxWidth: "300px" }}
                                            />
                                        </div>
                                    )}

                                    <div className="form-group">

                                        <label htmlFor="annotations" style={{ fontSize: "20px" }}>Annotations</label>

                                        <br />
                                        Keep CTRL pressed and click on the annotation to select multiple annotations

                                        <select
                                            className="form-control"
                                            id="annotations"
                                            value={this.state.annotations}
                                            onChange={this.handleAnnotationChange}
                                            name="annotations"
                                            multiple
                                        >
                                            {this.state.captions.map((annotate, index) => (
                                                <option key={index} value={annotate}>
                                                    {annotate}
                                                </option>
                                            ))}
                                        </select>
                                    </div>

                                    <button type="submit" className="btn btn-primary btn-block mt-4">
                                        Submit
                                    </button>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>

                <div className="row mt-5">
                    <div className="col-md-6">
                        <h3 className="text-center mb-4">Pending Images</h3>
                        <div className="image-grid">
                            {pendingImages.map((detail, id) => (
                                <div key={id} className="image-card">
                                    <div className="card-body">
                                        <h5 className="card-title">User: {detail.name}</h5>
                                        <img
                                            src={detail.image_url}
                                            alt="Image"
                                            className="img-fluid"
                                        />
                                        <div className="mt-3">
                                            <h6>Annotations:</h6>
                                            <ul className="list-unstyled">
                                                {detail.annotations.map((annotation, index) => (
                                                    <li key={index}>{annotation}</li>
                                                ))}
                                            </ul>
                                        </div>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                    {/* <div className="col-md-6 vertical-line"></div>  */}
                    <div className="col-md-6">
                        <h3 className="text-center mb-4">Annotated Images</h3>
                        <div className="image-grid">
                            {annotatedImages.map((detail, id) => (
                                <div key={id} className="image-card">
                                    <div className="card-body">
                                        <h5 className="card-title">User: {detail.name}</h5>
                                        <img
                                            src={detail.image_url}
                                            alt="Image"
                                            className="img-fluid"
                                        />
                                        <div className="mt-3">
                                            <h6>Annotations:</h6>
                                            <ul className="list-unstyled">
                                                {detail.annotations.map((annotation, index) => (
                                                    <li key={index}>{annotation}</li>
                                                ))}
                                            </ul>
                                        </div>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default HomePage;
