import Multiselect from 'multiselect-react-dropdown';
import React from "react";
import axios from "axios";

class App extends React.Component {
  state = {
    details: [],
    user: "",
    image: null, // State to store uploaded image
    imagePreview: null, // State to store image preview
    annotate: "", // State to store selected annotation
    captions: ["None", "airplanes", "cars", "birds", "cats", "deer", "dogs", "frogs", "horses", "ships", "trucks"], // Dropdown options
    annotations: ["None"],
  };

  componentDidMount() {
    let data;

    axios
      .get("http://localhost:8000/wel/")
      .then((res) => {
        data = res.data;
        this.setState({
          details: data,
        });
      })
      .catch((err) => { });
  }

  renderSwitch = (param) => {
    switch (param + 1) {
      case 1:
        return "primary ";
      case 2:
        return "secondary";
      case 3:
        return "success";
      case 4:
        return "danger";
      case 5:
        return "warning";
      case 6:
        return "info";
      default:
        return "yellow";
    }
  };

  handleInput = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  // handleImageChange = (e) => {
  //   const file = e.target.files[0];

  //   // Update image state
  //   this.setState({
  //     image: file,
  //   });

  //   // Create a FileReader instance
  //   const reader = new FileReader();

  //   // Listen for the FileReader load event
  //   reader.onloadend = () => {
  //     this.setState({
  //       imagePreview: reader.result, // Set imagePreview to the base64 encoded image data
  //     });
  //   };

  //   // Read the image file as a data URL
  //   if (file) {
  //     reader.readAsDataURL(file);
  //   }
  // };

  handleImageChange = (e) => {
    const files = e.target.files;

    // Update image state with an array of files
    this.setState({
      image: files,
    });

    // Create a FileReader instance for each file
    const imagePreviews = [];
    for (let i = 0; i < files.length; i++) {
      const reader = new FileReader();

      // Listen for the FileReader load event
      reader.onloadend = () => {
        // Set imagePreview to the base64 encoded image data for each file
        imagePreviews.push(reader.result);

        // If all files have been processed, update imagePreview state
        if (imagePreviews.length === files.length) {
          this.setState({
            imagePreview: imagePreviews,
          });
        }
      };

      // Read the image file as a data URL
      if (files[i]) {
        reader.readAsDataURL(files[i]);
      }
    }
  };


  handleSubmit = (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append("name", this.state.user);
    formData.append("image", this.state.image);
    // formData.append("annotate", this.state.annotate);
    formData.append("annotations", JSON.stringify(this.state.annotations)); // Convert annotations array to JSON string

    axios
      .post("http://localhost:8000/wel/", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      })
      .then((res) => {
        console.log("res = ", res);
        this.setState({
          user: "",
          image: null,
          imagePreview: null,
          // annotate: "",
          annotations: [],
        });
      })
      .catch((err) => {
        console.error("Error:", err);
      });
  };

  handleAnnotationChange = (e) => {
    const selectedOptions = Array.from(e.target.selectedOptions, (option) => option.value);
    this.setState({ annotations: selectedOptions });
  };


  render() {
    const pendingImages = this.state.details.filter(detail => detail.annotations.includes("None"));

    // Filter images with annotations other than "None"
    const annotatedImages = this.state.details.filter(detail => !detail.annotations.includes("None"));

    return (
      <div className="container jumbotron ">
        <form onSubmit={this.handleSubmit}>
          <div className="input-group mb-3">
            <div className="input-group-prepend">
              <span className="input-group-text" id="basic-addon1">
                {" "}
                Author{" "}
              </span>
            </div>
            <input
              type="text"
              className="form-control"
              placeholder="Name of the Poet/Author"
              aria-label="Username"
              aria-describedby="basic-addon1"
              value={this.state.user}
              name="user"
              onChange={this.handleInput}
            />
          </div>

          <div className="input-group mb-3">
            <div className="input-group-prepend">
              <span className="input-group-text">Upload Image</span>
            </div>
            <input
              type="file"
              className="form-control"
              onChange={this.handleImageChange}
              multiple
            />
          </div>

          {this.state.imagePreview && ( // Render image preview if available
            <div className="mb-3">
              <img
                src={this.state.imagePreview}
                alt="Preview"
                className="img-fluid"
                style={{ maxWidth: "200px" }}
              />
            </div>
          )}

          <div className="input-group mb-3">
            <div className="input-group-prepend">
              <span className="input-group-text">Caption</span>
            </div>
            <select
              className="form-control"
              value={this.state.annotations}
              onChange={this.handleAnnotationChange}
              name="annotations"
              multiple // Add the 'multiple' attribute to enable multiple selections
            >
              {this.state.captions.map((annotate, index) => (
                <option key={index} value={annotate}>
                  {annotate}
                </option>
              ))}
            </select>

          </div>

          <button type="submit" className="btn btn-primary mb-5">
            Submit
          </button>
        </form>

        <hr
          style={{
            color: "#000000",
            backgroundColor: "#000000",
            height: 0.5,
            borderColor: "#000000",
          }}
        />
<div className="container jumbotron">
  <div className="row">
    <div className="col-md-6">
      <h3>Pending</h3>
      {pendingImages.map((detail, id) => (
        <div key={id}>
          <h5>Author: {detail.name}</h5>
          <img
            src={`http://localhost:3000/set.png`}
            alt="Image"
            className="img-fluid mt-3"
          />
          <div className="annotations">
            <h5>Annotations:</h5>
            <ul>
              {detail.annotations.map((annotation, index) => (
                // printing the annotations
                console.log("imgpath = ", detail.image_path),
                console.log("annotation = ", annotation),
                <li key={index}>{annotation}</li>
              ))}
            </ul>
          </div>
          <span className="border border-primary"></span>
        </div>
      ))}
    </div>
    <div className="col-md-6">
      <h3>Annotated</h3>
      {annotatedImages.map((detail, id) => (
        <div key={id}>
          <h5>Author: {detail.name}</h5>
          <img
            src={`http://localhost:3000/set.png`}
            alt="Image"
            className="img-fluid mt-3"
          />
          <div className="annotations">
            <h5>Annotations:</h5>
            <ul>
              {detail.annotations.map((annotation, index) => (
                // printing the annotations
                console.log("imgpath = ", detail.image_path),
                console.log("annotation = ", annotation),
                <li key={index}>{annotation}</li>
              ))}
            </ul>
          </div>
          <span className="border border-primary"></span>
        </div>
      ))}
    </div>
  </div>
</div>

      </div>
    );
  }
}

export default App;
