import React from "react";
import axios from "axios";
// import imageSrc from "Set_List_.png"; // Import the image


class App extends React.Component {
  state = {
    details: [],
    user: "",
    image: null, // State to store uploaded image
    annotate: "", // State to store selected annotate
    captions: ["airplanes", "cars", "birds", "cats", "deer", "dogs", "frogs", "horses", "ships", "trucks"], // Dropdown options
  };

  componentDidMount() {
    let data;

    axios
      .get("http://localhost:8000/wel/")
      .then((res) => {
        data = res.data;
        this.setState({
          details: data,
        });
      })
      .catch((err) => { });
  }

  renderSwitch = (param) => {
    switch (param + 1) {
      case 1:
        return "primary ";
      case 2:
        return "secondary";
      case 3:
        return "success";
      case 4:
        return "danger";
      case 5:
        return "warning";
      case 6:
        return "info";
      default:
        return "yellow";
    }
  };

  handleInput = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  // handleImageChange = (e) => {
  //   this.setState({
  //     image: e.target.files,
  //   });
  // };

  handleImageChange = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();

    reader.onloadend = () => {
      this.setState({
        image: file,
        imagePreviewUrl: reader.result // Store the image preview URL in state
      });
    };

    if (file) {
      reader.readAsDataURL(file);
    }
  };

  handleSubmit = (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append("name", this.state.user);

    // for (let i = 0; i < this.state.image.length; i++) {
    //   formData.append("image", this.state.image[i]);
    // }

    formData.append("image", this.state.image[0])
    formData.append("annotate", this.state.annotate);

    axios
      .post("http://localhost:8000/wel/", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      })
      .then((res) => {
        console.log("res = ", res);
        this.setState({
          user: "",
          image: null,
          annotate: "",
        });
      })
      .catch((err) => {
        console.error("Error:", err);
      });
  };

  render() {
    let { imagePreviewUrl } = this.state;
    let $imagePreview = null;
    if (imagePreviewUrl) {
      $imagePreview = <img src={imagePreviewUrl} alt="Preview" className="img-fluid mt-3" />;
    } else {
      $imagePreview = <div className="previewText">Please select an Image for Preview</div>;
    }
    return (
      <div className="container jumbotron ">
        <form onSubmit={this.handleSubmit}>
          <div className="input-group mb-3">
            <div className="input-group-prepend">
              <span className="input-group-text"
                id="basic-addon1">
                {" "}
                Author{" "}
              </span>
            </div>
            <input type="text" className="form-control" multiple
              placeholder="Name of the Poet/Author"
              aria-label="Username"
              aria-describedby="basic-addon1"
              value={this.state.user} name="user"
              onChange={this.handleInput} />
            {/* <input type="file" className="form-control" multiple
              onChange={this.handleImageChange} /> */}

          </div>

          <div className="input-group mb-3">
            <div className="input-group-prepend">
              <span className="input-group-text">
                Upload Image
              </span>
            </div>
            <input type="file" className="form-control"
              onChange={this.handleImageChange} />
          </div>

          <div className="input-group mb-3">
            <div className="input-group-prepend">
              <span className="input-group-text">
                Caption
              </span>
            </div>
            <select className="form-control"
              value={this.state.annotate}
              onChange={this.handleInput} name="annotate">
              <option value="">Select annotate</option>
              {this.state.captions.map((annotate, index) => (
                <option key={index} value={annotate}>
                  {annotate}
                </option>
              ))}
            </select>
          </div>

          <button type="submit" className="btn btn-primary mb-5">
            Submit
          </button>
        </form>

        <hr
          style={{
            color: "#000000",
            backgroundColor: "#000000",
            height: 0.5,
            borderColor: "#000000",
          }}
        />

        {this.state.details.map((detail, id) => (
          <div key={id}>
            <img
              // src={"../../lucky/img/images/" + detail.image_path}
              src="Set_List_.png"
              height="200"
              width="200"

              // src={imageSrc}
              alt="Image"
              className="img-fluid mt-3"
            />
            <span className="border border-primary "></span>
          </div>
        ))}
      </div>
    );
  }
}
export default App;
